// Лабораторна робота № 5.4#include <iostream>#include <cmath>using namespace std;double S0(const int N){    double s = 1;    for (int k = N; k <= 25; k++)        s *= sqrt(1 + N / k);    return s;}double S1(const int N, const int k){    if (k > 25)        return 1;    else        return sqrt(1 + N / k) * S1(N, k + 1);}double S2(const int N, const int k){    if (k < N)        return 1;    else        return sqrt(1 + N / k) * S2(N, k - 1);}double S3(const int N, const int k, double t){    t = t * sqrt(1 + N / k);    if (k >= 25)        return t;    else        return S3(N, k + 1, t);}double S4(const int N, const int k, double t){    t = t * sqrt(1 + N / k);    if (k <= N)        return t;    else        return S4(N, k - 1, t);}int main(){    int N;    cout << "N = "; cin >> N;    cout << "(iter) S0 = " << S0(N) << endl;    cout << "(rec up ++) S1 = " << S1(N, N) << endl;    cout << "(rec up --) S2 = " << S2(N, 25) << endl;    cout << "(rec down ++) S3 = " << S3(N, N, 1) << endl;    cout << "(rec down --) S4 = " << S4(N, 25, 1) << endl;    return 0;}